mapper.select:
SELECT payInfo.pay_id, payInfo.building_id, payInfo.room_id, u.userid FROM ( SELECT p.pay_id, b.building_id, r.room_id, f.floor_id FROM (SELECT pay_id, pay_room_id, pay_date FROM pay
pay_date BETWEEN #{start} AND #{end}
) p JOIN building b ON 1=1 JOIN (SELECT room_id, room_floor_id FROM room
room_floor_id = #{floorId}
UNION SELECT r.room_id, r.room_floor_id FROM room r JOIN floor f ON r.room_floor_id = f.floor_id
f.belong_building = #{buildingId}
) r ON p.pay_room_id = r.room_id JOIN floor f ON r.room_floor_id = f.floor_id JOIN building b2 ON f.belong_building = b2.building_id ) payInfo LEFT JOIN ( SELECT userid, user_roomid FROM user
username LIKE CONCAT('%', #{username}, '%')
AND idCard LIKE CONCAT('%', #{idCard}, '%')
) u ON u.user_roomid = payInfo.floor_id
